const InvoiceGenerator = () =>{
    const DownloadInvoice = async () =>{
        try{
            const result = await easyinvoice.createInvoice(data);
            easyinvoice.download('Invoice.pdf', result.pdf);
        }
        catch(error){
            console.log(error);
        }
    }

    const RenderInvoice = async () =>{
        try{
            const result = await easyinvoice.createInvoice(data);
            const pdfWindow = window.open('');
            pdfWindow.document.write('<iframe width="100%" height="100%" src="data:application/pdf;base64,' + encodeURI(result.pdf) + '"></iframe>');
        }
        catch(error){
            console.log(error);
        }
    }
    return(
        <Container>
            <Form>
                <Row>
                    <Col>
                    <Button onClick={DownloadInvoice}>Descargar Factura</Button>
                    </Col>
                    <Col>
                    <Button onClick={RenderInvoice}>Renderizar Factura</Button>
                    </Col>
                </Row>
            </Form>
        </Container>
            );
};


export default InvoiceGenerator;